(23, 'download-039.jpg', 'Designer Desk Essentials', 'designer-desk-essentials', '<p>We\'re happy to offer you this awesome PSD mock-up that you can use to create custom hero images for your portfolio page, profile cover and more. All objects, shadows and background are separated so you can build your own composition with ease.</p>\r\n<p>The package also includes 4 ready-made PSD scenes and 2 seamless wooden textures.</p>', 'https://s3-us-west-1.amazonaws.com/getpixelapp/downloads/download-039.zip', 'url', NULL, NULL, NULL, 1, 'hero image, madebyme, mock-up, photo', 1, 0, 8, 10001, 10, 0, '2017-03-07 16:10:04', '2015-09-13 09:02:50', '2017-03-07 09:10:04'),
(24, 'download-001.jpg', 'Free PSD: Ui Kit', 'free-psd-ui-kit', '<p>Here\'s a UI Kit brought to you by David Minty. This UI Kit features a video player, graphs, forms, maps, and so much more. Feel free to download and use as you wish :)</p>', 'https://s3-us-west-1.amazonaws.com/getpixelapp/downloads/download-001.zip', 'url', NULL, NULL, NULL, 1, 'psd, uikit, interface, freebie', 1, 0, 2, 1, 0, 0, '2017-02-07 16:48:30', '2015-09-13 09:02:51', '2017-02-07 09:48:30'),
